import React from 'react'
import Link from 'next/link'
import useSWR from 'swr'

const fetcher = (...args) => fetch(...args).then(res => res.json())

const Index = () => {
  const {data, error} = useSWR('/api/get-promo',fetcher)

  return(
  <div>
    <p className='mt-12 text-center'  >Estamos sempre atentos para ouvir nossos clientes e melhorarmos nossos servicos. E ainda te dar Prêmios</p>
    <div className='text-center my-12' >
      <Link href='/pesquisa'>
       <a className=' transition ease-in duration-700 bg-blue-300 px-12 py-4 font-bold rounded-lg shadow-lg hover:shadow' >Faça sua Crítica ou Sugestão </a>
        </Link>
    </div>
    { !data  && <p>Carregando...</p> }
    {!error && data && data.showCoupon &&
    <p className='mt-12 text-center'>{data.message}</p>
    }
 </div>)
}

export default Index
