import React, { useState } from 'react'
import Link from 'next/link'

const Pesquisa = () => {
  const [ form, setForm ] = useState({
    Nome: 'aaaa',
    Email: 'bbbb',
    Whatsapp: 'cccc'
  })
  const save = async () => {
    
    try{
    const response = await fetch('/api/save',{
       method: 'POST',
       body: JSON.stringify(form)
   })
   const data = await response.json()
   console.log(data)
}catch(err){

  }
}
const onChange = evt => {
  console.log(evt.target)
}
    return (
        <div className='pt-6' >
            <h1 className='text-center font-bold my-6 text-2xl mb-6' >Críticas e Sugestões</h1>
            <p className='text-center'>Esse estabelecimento está atento a todas as sugestões dos nossos clientes.<br/> E estamos prontos para melhor atender-los </p>
            <div className='w-1/5 mx-auto' >
              <label className='font-bold' >Seu Nome:</label>

              <input type='text' className='p-4 block shadow bg-blue-100 my-2 rounded' placeholder='Nome' onChange={onChange} />

              <label className='font-bold' >Email:</label>
              <input type='text' className='p-4 block shadow bg-blue-100 my-2 rounded' placeholder='Email' />

              <label className='font-bold' >Whatsapp:</label>
              <input type='text' className='p-4 block shadow bg-blue-100 my-2 rounded' placeholder='Whatsapp' />

              <button className='bg-blue-300 px-12 py-4 font-bold rounded-lg shadow-lg hover:shadow' onClick={save}>Salvar</button>
            </div>
        </div>
    )
}
export default Pesquisa
